<?php	require_once('./config/dbconfig.php');	session_start();	//echo json_encode($_POST);	$con = mysql_connect(host,username,password);	$userid = $_SESSION['USER_ID'];	$classid = $_POST['classid'];	$charpter = $_POST['charpter'];	$lesson = $_POST['lesson'];		if (!$con)		{			die('Could not connect: ' . mysql_error());		}	mysql_select_db(dbname, $con);		mysql_query("set names 'gbk'");	$sql = "SELECT * FROM T_STUDENT_REMARK 	WHERE user_id = $userid AND	class_id = $classid AND 	charpter_id = $charpter AND	lesson_id = $lesson";	$result = mysql_query($sql);		$studentRemark=' ';	$i=0;	while($row = mysql_fetch_array($result)){		$studentRemark[$i]['pageNum']= $row['page_num'];		$studentRemark[$i]['pleft'] = $row['pleft'];		$studentRemark[$i]['ptop'] = $row['ptop'];		$studentRemark[$i]['pstartx'] = $row['pstartx'];		$studentRemark[$i]['pstarty'] = $row['pstarty'];		$studentRemark[$i]['pendx'] = $row['pendx'];		$studentRemark[$i]['pendy'] = $row['pendy'];		$studentRemark[$i]['content']  = iconv('gbk//IGNORE','UTF-8',$row['content']);		$i++;	}	echo json_encode($studentRemark);	mysql_close($con);?>